import React from 'react';
import './App.css';
import Tasks from './xample/tasks.json';
import Task from './component/Tasks'

class App extends React.Component {

  state = {
    tasks: Tasks
  }

  Hola(){
    return (<div>Hola mundo</div>)
  }

  render() {
    return <div>
      {<Task tasks={this.state.tasks} />}
      <p><this.Hola/></p>
    </div>
  }
}

export default App;
