import React, { Component } from 'react';

class TaskForm extends Component{
    render(){
        return <form>
            <input type = "text" placeholder = "Escriba lo que vayas a escribir." style = {{padding:'1em',margin:'2em'}} />
            <textarea cols = "20" rows = "10" placeholder = "Escriba la descripcion."></textarea>
            <br/><br/>
            <input type = "submit" value = "Enviar data" />
        </form>
    }
}

export default TaskForm;
