import React from 'react';
import './App.css';
import Tasks from './xample/tasks.json';

class App extends React.Component {

  state = {
    tasks: Tasks
  }

  render() {
    return <div>
      {this.state.tasks.map(response => <ul>
        <li>{response.id}</li>
        <li>{response.task}</li>
        <li>{response.description}</li>
        <li>{response.status}</li>
      </ul>)}
    </div>
  }
}

export default App;
