class Funct extends React.Component {

  status = {
    show: false
  }

  toggleShow = () => {
    this.setState({ show: !this.status.show });
  }

  render() {
    if (this.status.show) {
      return <div>
          <h1> {this.props.prop} </h1>
          <h2> {this.props.carlota} </h2>
          <button onClick={this.toggleShow}>Click</button>
        </div>

    } else {
      return <div><h3>Nothing</h3>
        <button onClick={this.toggleShow}>Click</button>
      </div>
    }
  }
}


function App() {
  return (
    <h1>Hello Work
      <Funct prop="Este" />
      <Funct carlota="Diomedez Diaz" />  </h1>
  );
}

export default App;
