import React from 'react';
import './App.css';
import Tasks from './xample/tasks.json';
import Task from './component/Tasks'

class App extends React.Component {

  state = {
    tasks: Tasks
  }

  render() {
    return <div>
      {this.state.tasks.map(response => <ul key = {response.id}>
        <li>{response.id}</li>
        <li>{response.task}</li>
        <li>{response.description}</li>
        <li>{response.status}</li>
        <li><Task/></li>
      </ul>)}
    </div>
  }
}

export default App;
