import React, { Component } from 'react';
import '../css/task.css'
import PropTypes from 'prop-types';
import TaskForm from './TaskForm'

class Task extends Component {

    // React: se agregan valores tambien de esta manera en forma lineal
    // dentro de la estiqueta, se declara un object y se le pasa a la etiqueta
    Style() {
        return {
            background: this.props.task.status ? 'rgba(0,0,0,0.7)' : 'blue'
        }
    }

    render() {
        // Cuando se resive una propiedad se debe llamar con la palabra reservada
        // 'props' y obviamente el this.props y luego la propiedad que nos mandan 
        // que en este caso es 'task'
        return <div>
            <section>
                <TaskForm style = {{padding:'2em',margin:'2em'}} />
            </section>
            <ul id="container">
                <li className="lista">{this.props.task.id}</li>
                <li className="lista">{this.props.task.task}</li>
                <li className="lista" style={this.Style()}>{this.props.task.description}</li>
                <li className="lista" style={{ background: 'black' }}>{this.props.task.status}</li>
                <li className="lista">
                    <input type="checkbox" />
                    <button>X</button>
                </li>
            </ul>
        </div>
    }
}

// Los propsTypes: sirve para designar que tipo de dato espero, en este ejemplo le digo
// que espero un object
Task.propTypes = {
    task: PropTypes.object.isRequired
}

export default Task;
