import React, { Component } from 'react';
import '../css/task.css'

class Task extends Component {

    style = {
        background: this.props.task.status ? 'red' : 'blue'
    }

    render() {
        return <ul id = "container">
            <li className="lista">{this.props.task.id}</li>
            <li className="lista">{this.props.task.task}</li>
            <li className="lista" style = {{background}}>{this.props.task.description}</li>
            <li className="lista">{this.props.task.status}</li>
            <li className="lista">
                <input type="checkbox" />
                <button>X</button>
            </li>
        </ul>
    }
}

export default Task;
