import React, { Component } from 'react';
import '../css/task.css'
import PropTypes from 'prop-types';

class Task extends Component {

    Style(){
    return {
        background: this.props.task.status ? 'rgba(255,255.255,0.4)' : 'blue'
    }
}

    render() {
        return <ul id = "container">
            <li className="lista">{this.props.task.id}</li>
            <li className="lista">{this.props.task.task}</li>
            <li className="lista" style = {this.Style()}>{this.props.task.description}</li>
            <li className="lista" style = {{background:'black'}}>{this.props.task.status}</li>
            <li className="lista">
                <input type="checkbox" />
                <button>X</button>
            </li>
        </ul>
    }
}

//Task.PropTypes

export default Task;
