import React, { Component } from 'react';

class TaskForm extends Component {

    state = {
        title: '',
        descripcion: ''
    }

    onSubmit(event) {
        event.preventDefault();
        console.log("Data...")
    }

    OnChange(event) {
        this.setState(
            { 
                title : event.target.value,
                descripcion : event.target.value,
            }
            );
    }

    render() {
        return <form onSubmit={this.onSubmit}>
            <input type="text" placeholder="Escriba lo que vayas a escribir." style={{ padding: '1em', margin: '2em' }} onChange={this.OnChange} value={this.state.title} />
            <textarea cols="20" rows="10" placeholder="Escriba la descripcion." onChange={this.OnChange} value={this.state.descripcion} ></textarea>
            <br />
            <input type="submit" value="Enviar data" />
        </form>
    }
}

export default TaskForm;
